ect(array("prom_cnd_id"=>$prom_cnd_id))){				$info["prom_cnd_id"]=$obj_pds_list_prom_cond->getprom_cnd_id();				$info["prom_cnd_promotion"]=$obj_pds_list_prom_cond->getprom_cnd_promotion();				$info["prom_cnd_cond_type"]=$obj_pds_list_prom_cond->getprom_cnd_cond_type();				$info["prom_cnd_wkdy_sunday"]=$obj_pds_list_prom_cond->getprom_cnd_wkdy_sunday();				$info["prom_cnd_wkdy_monday"]=$obj_pds_list_prom_cond->getprom_cnd_wkdy_monday();				$info["prom_cnd_wkdy_tuesday"]=$obj_pds_list_prom_cond->getprom_cnd_wkdy_tuesday();				$info["prom_cnd_wkdy_wednesday"]=$obj_pds_list_prom_cond->getprom_cnd_wkdy_wednesday();				$info["prom_cnd_wkdy_thursday"]=$obj_pds_list_prom_cond->getprom_cnd_wkdy_thursday();				$info["prom_cnd_wkdy_friday"]=$obj_pds_list_prom_cond->getprom_cnd_wkdy_friday();				$info["prom_cnd_wkdy_saturday"]=$obj_pds_list_prom_cond->getprom_cnd_wkdy_saturday();				$info["prom_cnd_daytime_from"]=$obj_pds_list_prom_cond->getprom_cnd_daytime_from();				$info["prom_cnd_daytime_to"]=$obj_pds_list_prom_cond->getprom_cnd_daytime_to();				$info["prom_cnd_start_date"]=$obj_pds_list_prom_cond->getprom_cnd_start_date();				$info["prom_cnd_end_date"]=$obj_pds_list_prom_cond->getprom_cnd_end_date();				$info["isActive"] = 0;				//..check deactive date is not set or 0				if((is_not_empty($obj_pds_list_prom_cond->getpds_list_prom_cond_deactive_date())==false)  || (is_gt_zero_num(strtotime($obj_pds_list_prom_cond->getpds_list_prom_cond_deactive_date()))== false)){					$info["isActive"] = 1;				}else{					//..check the deactive date is greater than todays date					if(strtotime($obj_pds_list_prom_cond->getpds_list_prom_cond_deactive_date()) > strtotime(date(DATE_FORMAT))){						$info["isActive"] = 1;					}				}			}		unset($obj_pds_list_prom_cond);		return $info;		}	}//..End GetInfo	public static function GetFields($data){		global $pds_list_prom_cond_active_condition;		$arr = array();		if(is_not_empty($data)){			$qry ="SELECT ".$data['key_field'].",".$data['value_field']." FROM ".PDS_LIST_PROM_COND."";			if(is_gt_zero_num($data['isActive'])){				$qry .= " WHERE ".$pds_list_prom_cond_active_condition;			}			$res = mysql_query($qry); 			if($res){				while($row=mysql_fetch_assoc($res)){					$arr[$row[$data['key_field']]] = $row[$data['value_field']];				}			}		}		return $arr;	}//.. End of GetFields}//..End pds_list_prom_cond?>