.end deactivate	public static function GetInfo($complnt_id) {		$info = array();		if($complnt_id != ""){			$obj_tbl_complaints = new tbl_complaints();			if($obj_tbl_complaints->readObject(array("complnt_id"=>$complnt_id))){				$info["complnt_id"]=$obj_tbl_complaints->getcomplnt_id();				$info["complnt_title"]=$obj_tbl_complaints->getcomplnt_title();				$info["complnt_description"]=$obj_tbl_complaints->getcomplnt_description();				$info["complnt_email"]=$obj_tbl_complaints->getcomplnt_email();				$info["complnt_phone"]=$obj_tbl_complaints->getcomplnt_phone();				$info["complnt_restaurant"]=$obj_tbl_complaints->getcomplnt_restaurant();				$info["complnt_user_id"]=$obj_tbl_complaints->getcomplnt_user_id();				$info["complnt_user_type"]=$obj_tbl_complaints->getcomplnt_user_type();				$info["complnt_table"]=$obj_tbl_complaints->getcomplnt_table();				if(is_gt_zero_num($info["complnt_table"])){					$table_info = tbl_dining_table::GetInfo($info["complnt_table"]);					$info["complnt_table_nm"]=$table_info['table_number'];				}												$info["complnt_start_date"]=$obj_tbl_complaints->getcomplnt_start_date();				$info["complnt_end_date"]=$obj_tbl_complaints->getcomplnt_end_date();				$info["isActive"] = 0;				//..check deactive date is not set or 0				if((is_not_empty($obj_tbl_complaints->gettbl_complaints_deactive_date())==false)  || (is_gt_zero_num(strtotime($obj_tbl_complaints->gettbl_complaints_deactive_date()))== false)){					$info["isActive"] = 1;				}else{					//..check the deactive date is greater than todays date					if(strtotime($obj_tbl_complaints->gettbl_complaints_deactive_date()) > strtotime(date(DATE_FORMAT))){						$info["isActive"] = 1;					}				}			}		unset($obj_tbl_complaints);		return $info;		}	}//..End GetInfo	public static function GetFields($data){		global $tbl_complaints_active_condition;		$arr = array();		if(is_not_empty($data)){			$qry ="SELECT ".$data['key_field'].",".$data['value_field']." FROM ".TBL_COMPLAINTS."";			if(is_gt_zero_num($data['isActive'])){				$qry .= " WHERE ".$tbl_complaints_active_condition;			}			$res = mysql_query($qry); 			if($res){				while($row=mysql_fetch_assoc($res)){					$arr[$row[$data['key_field']]] = $row[$data['value_field']];				}			}		}		return $arr;	}//.. End of GetFields}//..End tbl_complaints?>