tion GetActiveStaff(){		$arr = array();		$res = mysql_query("SELECT staff_id, CONCAT(`staff_fname`,' ',`staff_lname`) 'Staff_Member' FROM ".HM_TBL_STAFF." WHERE staff_start_date > 0AND ( staff_end_date > CURDATE() OR staff_end_date =0 OR staff_end_date IS NULL)");		if($res){			while($row=mysql_fetch_assoc($res)){				$arr[$row['staff_id']] = $row['Staff_Member'];			}		}		return $arr;	}	}?>