				$this->setlist_id($list_id);				$this->settitle($title);				$this->setpdf($pdf);				$this->setcomments($comments);				$this->setstart_date($start_date);				$this->setend_date($end_date);				$this->setstates($states);				$this->setmetro_area($metro_area);				$this->setpromotion_id($promotion_id);				$this->setimg_ext($img_ext);				$this->setviews_count($views_count);				$this->setis_pdf_uploaded($is_pdf_uploaded);				$this->setcupon_type($cupon_type);				$this->setallowed_cupons($allowed_cupons);				$this->setredimed_cupons($redimed_cupons);				$this->insert();				return $this->getid();			}		}		return OPERATION_FAIL;	}//..create	public function update($id, $list_id, $title, $pdf, $comments, $start_date, $end_date, $states, $metro_area, $promotion_id, $img_ext, $views_count, $is_pdf_uploaded, $cupon_type, $allowed_cupons, $redimed_cupons) {		if(is_gt_zero_num($id)){			if ($this->readObject(array(ID=>$id))){				$this->setlist_id($list_id);				$this->settitle($title);				$this->setpdf($pdf);				$this->setcomments($comments);				$this->setstart_date($start_date);				$this->setend_date($end_date);				$this->setstates($states);				$this->setmetro_area($metro_area);				$this->setpromotion_id($promotion_id);				$this->setimg_ext($img_ext);				$this->setviews_count($views_count);				$this->setis_pdf_uploaded($is_pdf_uploaded);				$this->setcupon_type($cupon_type);				$this->setallowed_cupons($allowed_cupons);				$this->setredimed_cupons($redimed_cupons);				$this->insert();				return OPERATION_SUCCESS;			}		}		return OPERATION_FAIL;	}//..update	public function activate($id){		if(is_gt_zero_num($id)){			if ($this->readObject(array(ID=>$id))){				$qry  = "UPDATE ".TBL_LIST_PROMOTIONS.RET."SET".RET."			".TBL_LIST_PROMOTIONS_DEACTIVE_DATE." = '".date(EMPTY_DATE_FORMAT)."' WHERE ".ID."={$id}";				$res = mysql_query($qry);				if($res){					return 1;				}			}		}		return 0;	}//..end activate	public function deactivate($id){		if(is_gt_zero_num($id)){			if ($this->readObject(array(ID=>$id))){				$qry  = "UPDATE ".TBL_LIST_PROMOTIONS.RET."SET".RET."			".TBL_LIST_PROMOTIONS_DEACTIVE_DATE." = '".date(DATE_FORMAT)."' WHERE ".ID."={$id}";				$res = mysql_query($qry);				if($res){					return 1;				}			}		}		return 0;	}//..end deactivate	public static function GetInfo($id) {		$info = array();		if($id != ""){			$obj_tbl_list_promotions = new tbl_list_promotions();			if($obj_tbl_list_promotions->readObject(array("id"=>$id))){				$info["id"]=$obj_tbl_list_promotions->getid();				$info["list_id"]=$obj_tbl_list_promotions->getlist_id();				$info["title"]=$obj_tbl_list_promotions->gettitle();				$info["pdf"]=$obj_tbl_list_promotions->getpdf();				$info["comments"]=$obj_tbl_list_promotions->getcomments();				$info["start_date"]=$obj_tbl_list_promotions->getstart_date();				$info["end_date"]=$obj_tbl_list_promotions->getend_date();				$info["states"]=$obj_tbl_list_promotions->getstates();				$info["metro_area"]=$obj_tbl_list_promotions->getmetro_area();				$info["promotion_id"]=$obj_tbl_list_promotions->getpromotion_id();				$info["img_ext"]=$obj_tbl_list_promotions->getimg_ext();				$info["views_count"]=$obj_tbl_list_promotions->getviews_count();				$info["is_pdf_uploaded"]=$obj_tbl_list_promotions->getis_pdf_uploaded();				$info["cupon_type"]=$obj_tbl_list_promotions->getcupon_type();				$info["allowed_cupons"]=$obj_tbl_list_promotions->getallowed_cupons();				$info["redimed_cupons"]=$obj_tbl_list_promotions->getredimed_cupons();				$info["isActive"] = 0;				//..check deactive date is not set or 0				if((is_not_empty($obj_tbl_list_promotions->gettbl_list_promotions_deactive_date())==false)  || (is_gt_zero_num(strtotime($obj_tbl_list_promotions->gettbl_list_promotions_deactive_date()))== false)){					$info["isActive"] = 1;				}else{					//..check the deactive date is greater than todays date					if(strtotime($obj_tbl_list_promotions->gettbl_list_promotions_deactive_date()) >= strtotime(date(DATE_FORMAT))){						$info["isActive"] = 1;					}				}			}		unset($obj_tbl_list_promotions);		return $info;		}	}//..End GetInfo	public static function GetFields($data){		global $tbl_list_promotions_active_condition;		$arr = array();		if(is_not_empty($data)){			$qry ="SELECT ".$data['key_field'].",".$data['value_field']." FROM ".TBL_DINING_TABLE."";			if(is_gt_zero_num($data['isActive'])){				$qry .= " WHERE ".$tbl_list_promotions_active_condition;			}			$res = mysql_query($qry); 			if($res){				while($row=mysql_fetch_assoc($res)){					$arr[$row[$data['key_field']]] = $row[$data['value_field']];				}			}		}			return $arr;	}//.. End of GetFields		public static function cupon_types(){		return array(						"none"=>"Regular promotions",					"all_site"=>"One time coupon",					"recommendation"=>"coupon on recommendation",					"survey"=>"coupon on survey",					"reward"=>"Rewards program"				);	}	}//..End tbl_list_promotions?>