hydrate);				$this->setnutr_sugar($nutr_sugar);				$this->setnutr_dietary_fiber($nutr_dietary_fiber);				$this->setnutr_protein($nutr_protein);				$this->setnutr_start_date(date(DATE_FORMAT));				$this->insert();				return $this->getnutr_id();			}		}		return OPERATION_FAIL;	}//..create	public function update($nutr_id, $nutr_dish_id, $nutr_total_cal, $nutr_cal_from_fat, $nutr_total_fat, $nutr_saturated_fat, $nutr_trans_fat, $nutr_cholestrol, $nutr_sodium, $nutr_total_carbohydrate, $nutr_sugar, $nutr_dietary_fiber, $nutr_protein, $nutr_start_date, $nutr_end_date) {		if(is_gt_zero_num($nutr_id)){			if ($this->readObject(array(NUTR_ID=>$nutr_id))){				$this->setnutr_dish_id($nutr_dish_id);				$this->setnutr_total_cal($nutr_total_cal);				$this->setnutr_cal_from_fat($nutr_cal_from_fat);				$this->setnutr_total_fat($nutr_total_fat);				$this->setnutr_saturated_fat($nutr_saturated_fat);				$this->setnutr_trans_fat($nutr_trans_fat);				$this->setnutr_cholestrol($nutr_cholestrol);				$this->setnutr_sodium($nutr_sodium);				$this->setnutr_total_carbohydrate($nutr_total_carbohydrate);				$this->setnutr_sugar($nutr_sugar);				$this->setnutr_dietary_fiber($nutr_dietary_fiber);				$this->setnutr_protein($nutr_protein);				$this->insert();				return OPERATION_SUCCESS;			}		}		return OPERATION_FAIL;	}//..update	public function activate($nutr_id){		if(is_gt_zero_num($nutr_id)){			if ($this->readObject(array(NUTR_ID=>$nutr_id))){				$qry  = "UPDATE ".TBL_NUTRITION.RET."SET".RET."			".TBL_NUTRITION_DEACTIVE_DATE." = '".date(EMPTY_DATE_FORMAT)."' WHERE ".NUTR_ID."={$nutr_id}";				$res = mysql_query($qry);				if($res){					return 1;				}			}		}		return 0;	}//..end activate	public function deactivate($nutr_id){		if(is_gt_zero_num($nutr_id)){			if ($this->readObject(array(NUTR_ID=>$nutr_id))){				$qry  = "UPDATE ".TBL_NUTRITION.RET."SET".RET."			".TBL_NUTRITION_DEACTIVE_DATE." = '".date(DATE_FORMAT)."' WHERE ".NUTR_ID."={$nutr_id}";				$res = mysql_query($qry);				if($res){					return 1;				}			}		}		return 0;	}//..end deactivate	public static function GetInfo($nutr_id,$nutr_dish_id=0) {		$info = array();		$filter_arr=array();		if(is_gt_zero_num($nutr_id)){			$filter_arr[NUTR_ID]=$nutr_id;		}		if(is_gt_zero_num($nutr_dish_id)){			$filter_arr[NUTR_DISH_ID]=$nutr_dish_id;		}				if(is_not_empty($filter_arr)){			$obj_tbl_nutrition = new tbl_nutrition();			if($obj_tbl_nutrition->readObject($filter_arr)){				$info["nutr_id"]=$obj_tbl_nutrition->getnutr_id();				$info["nutr_dish_id"]=$obj_tbl_nutrition->getnutr_dish_id();				$info["nutr_total_cal"]=$obj_tbl_nutrition->getnutr_total_cal();				$info["nutr_cal_from_fat"]=$obj_tbl_nutrition->getnutr_cal_from_fat();				$info["nutr_total_fat"]=$obj_tbl_nutrition->getnutr_total_fat();				$info["nutr_saturated_fat"]=$obj_tbl_nutrition->getnutr_saturated_fat();				$info["nutr_trans_fat"]=$obj_tbl_nutrition->getnutr_trans_fat();				$info["nutr_cholestrol"]=$obj_tbl_nutrition->getnutr_cholestrol();				$info["nutr_sodium"]=$obj_tbl_nutrition->getnutr_sodium();				$info["nutr_total_carbohydrate"]=$obj_tbl_nutrition->getnutr_total_carbohydrate();				$info["nutr_sugar"]=$obj_tbl_nutrition->getnutr_sugar();				$info["nutr_dietary_fiber"]=$obj_tbl_nutrition->getnutr_dietary_fiber();				$info["nutr_protein"]=$obj_tbl_nutrition->getnutr_protein();				$info["nutr_start_date"]=$obj_tbl_nutrition->getnutr_start_date();				$info["nutr_end_date"]=$obj_tbl_nutrition->getnutr_end_date();				$info["isActive"] = 0;				//..check deactive date is not set or 0				if((is_not_empty($obj_tbl_nutrition->gettbl_nutrition_deactive_date())==false)  || (is_gt_zero_num(strtotime($obj_tbl_nutrition->gettbl_nutrition_deactive_date()))== false)){					$info["isActive"] = 1;				}else{					//..check the deactive date is greater than todays date					if(strtotime($obj_tbl_nutrition->gettbl_nutrition_deactive_date()) > strtotime(date(DATE_FORMAT))){						$info["isActive"] = 1;					}				}			}		unset($obj_tbl_nutrition);		return $info;		}	}//..End GetInfo	public static function GetFields($data){		global $tbl_nutrition_active_condition;		$arr = array();		if(is_not_empty($data)){			$qry ="SELECT ".$data['key_field'].",".$data['value_field']." FROM ".TBL_NUTRITION."";			if(is_gt_zero_num($data['isActive'])){				$qry .= " WHERE ".$tbl_nutrition_active_condition;			}			$res = mysql_query($qry); 			if($res){				while($row=mysql_fetch_assoc($res)){					$arr[$row[$data['key_field']]] = $row[$data['value_field']];				}			}		}		return $arr;	}//.. End of GetFields}//..End tbl_nutrition?>