ic static function chkIfPromAlreadyApplied($order_id,$promotion_id){		$fnd=0;	//..First check if it is applied to whole order discount	$result = mysql_query("SELECT COUNT(".ORDPROM_ID.") `cnt` FROM ".TBL_ORDER_PROMOTIONS." WHERE ".ORDPROM_ORDER."={$order_id} AND ".ORDPROM_PROMOTION."={$promotion_id} AND ".ORDPROM_DISCOUNT.">0 AND ".ORDPROM_ORDER_SBMNU_DISH."=0;");	$fnd=mysql_result($result, 0); 	if($fnd){		return 1;	}else{					//..get the the order item list		$order_items=tbl_order_promotions::getOrderItems($order_id);		//..get the the order promotion item list		$ord_prom_items=tbl_order_promotions::getOrderPromotionItems($order_id,$promotion_id);		//..Check if both are same		if((is_not_empty($orde_items)) && (is_not_empty($ord_prom_items))){			if($orde_items==$ord_prom_items){				return 1;			}		}		}	return $fnd;		 }   /** * Get the order promotion items * @param integer $order_id * @param integer $promotion_id * @return string */     public static function getOrderPromotionItems($order_id,$promotion_id){			$items='';				$listof_submenu = '';		//..Get the Promotions submenu Discount 		$tmpres  = mysql_query('SELECT GROUP_CONCAT('.PRMDISC_BOGO_SBMNU.')  FROM '.TBL_PROM_DISCOUNTS.' WHERE '.PRMDISC_BOGO_SBMNU_DISH.' = 0 AND '.PRMDISC_PROMOTION.'='.$promotion_id);		if($tmpres && mysql_num_rows($tmpres)){			$listof_submenu = mysql_result($tmpres,0);		} 		unset($tmpres);				$listof_order_items = '';		if(is_not_empty($listof_submenu)){			$tmpres = mysql_query('SELECT GROUP_CONCAT('.ORDER_DTL_ID.') FROM'.TBL_ORDER_DETAILS.' WHERE '.ORDER_DTL_ORDER_ID.'='.$order_id.' AND '.ORDER_DTL_SUBMENU_DISH_ID.' IN (SELECT '.SBMNU_DISH_ID.' FROM '.TBL_SUBMENU_DISHES.' WHERE '.SBMNU_DISH_SUBMENU.' IN ('.$listof_submenu.')) ');			if($tmpres && mysql_num_rows($tmpres)){				$listof_order_items = mysql_result($tmpres,0);			} 			unset($tmpres);		}						$qry="SELECT GROUP_CONCAT(`ordprom_order_sbmnu_dish`) FROM ".TBL_ORDER_PROMOTIONS."  WHERE ".ORDPROM_ORDER."={$order_id} AND ".ORDPROM_PROMOTION."={$promotion_id} AND ".ORDPROM_DISCOUNT.">0 AND ".ORDPROM_ORDER_SBMNU_DISH.">0";		$res = mysql_query($qry); 		if($res){			$items=mysql_result($res, 0); 		}		return $items;   } 	 	 	 	  	 	    //..Get the order items   public static function getOrderItems($order_id){			$items='';		$qry='SELECT GROUP_CONCAT(`ord_dtl_id`) FROM `tbl_order_details` WHERE `ord_dtl_order_id`='.$order_id;		$res = mysql_query($qry); 		if($res){			$items=mysql_result($res, 0); 		}		return $items;   }	     }//..End tbl_order_promotions?>