om_reminder->getprem_after();				$info["prem_spc_date"]=$obj_tbl_prom_reminder->getprem_spc_date();				$info["prem_act_send_dt"]=$obj_tbl_prom_reminder->getprem_act_send_dt();				$info["prem_is_send"]=$obj_tbl_prom_reminder->getprem_is_send();								$info["prem_phone"]=$obj_tbl_prom_reminder->getprem_phone();								$info["prem_start_date"]=$obj_tbl_prom_reminder->getprem_start_date();				$info["prem_end_date"]=$obj_tbl_prom_reminder->getprem_end_date();				$info["isActive"] = 0;				//..check deactive date is not set or 0				if((is_not_empty($obj_tbl_prom_reminder->gettbl_prom_reminder_deactive_date())==false)  || (is_gt_zero_num(strtotime($obj_tbl_prom_reminder->gettbl_prom_reminder_deactive_date()))== false)){					$info["isActive"] = 1;				}else{					//..check the deactive date is greater than todays date					if(strtotime($obj_tbl_prom_reminder->gettbl_prom_reminder_deactive_date()) > strtotime(date(DATE_FORMAT))){						$info["isActive"] = 1;					}				}			}		unset($obj_tbl_prom_reminder);		return $info;		}	}//..End GetInfo	public static function GetFields($data){		global $tbl_prom_reminder_active_condition;		$arr = array();		if(is_not_empty($data)){			$qry ="SELECT ".$data['key_field'].",".$data['value_field']." FROM ".TBL_PROM_REMINDER."";			if(is_gt_zero_num($data['isActive'])){				$qry .= " WHERE ".$tbl_prom_reminder_active_condition;			}			$res = mysql_query($qry); 			if($res){				while($row=mysql_fetch_assoc($res)){					$arr[$row[$data['key_field']]] = $row[$data['value_field']];				}			}		}		return $arr;	}//.. End of GetFields}//..End tbl_prom_reminder?>