u_id;				}		if(is_gt_zero_num($rst_mnu_restaurant)){			 $arr[RST_MNU_RESTAURANT] = $rst_mnu_restaurant;				}				if(is_not_empty($arr)){			$obj_tbl_rest_menu_opt = new tbl_rest_menu_opt();			if($obj_tbl_rest_menu_opt->readObject($arr)){				$info["rst_mnu_id"]=$obj_tbl_rest_menu_opt->getrst_mnu_id();				$info["rst_mnu_restaurant"]=$obj_tbl_rest_menu_opt->getrst_mnu_restaurant();				$info["rst_mnu_add_order"]=$obj_tbl_rest_menu_opt->getrst_mnu_add_order();				$info["rst_mnu_add_prom"]=$obj_tbl_rest_menu_opt->getrst_mnu_add_prom();				$info["rst_mnu_serv_req"]=$obj_tbl_rest_menu_opt->getrst_mnu_serv_req();				$info["rst_mnu_orders"]=$obj_tbl_rest_menu_opt->getrst_mnu_orders();				$info["rst_mnu_tbl_statuses"]=$obj_tbl_rest_menu_opt->getrst_mnu_tbl_statuses();				$info["rst_mnu_prom_claimed"]=$obj_tbl_rest_menu_opt->getrst_mnu_prom_claimed();				$info["rst_mnu_complaints"]=$obj_tbl_rest_menu_opt->getrst_mnu_complaints();				$info["rst_mnu_transfer_tbl"]=$obj_tbl_rest_menu_opt->getrst_mnu_transfer_tbl();				$info["rst_mnu_online_users"]=$obj_tbl_rest_menu_opt->getrst_mnu_online_users();				$info["rst_mnu_loyalty_rewards"]=$obj_tbl_rest_menu_opt->getrst_mnu_loyalty_rewards();				$info["rst_mnu_configuration"]=$obj_tbl_rest_menu_opt->getrst_mnu_configuration();				$info["rst_mnu_reward_conf"]=$obj_tbl_rest_menu_opt->getrst_mnu_reward_conf();				$info["rst_mnu_allergy"]=$obj_tbl_rest_menu_opt->getrst_mnu_allergy();								$info["rst_mnu_start_date"]=$obj_tbl_rest_menu_opt->getrst_mnu_start_date();				$info["rst_mnu_end_date"]=$obj_tbl_rest_menu_opt->getrst_mnu_end_date();								$info["rst_mnu_restaurant_nm"]=$obj_tbl_rest_menu_opt->getrst_mnu_restaurant_nm();				$info["isActive"] = 0;				//..check deactive date is not set or 0				if((is_not_empty($obj_tbl_rest_menu_opt->gettbl_rest_menu_opt_deactive_date())==false)  || (is_gt_zero_num(strtotime($obj_tbl_rest_menu_opt->gettbl_rest_menu_opt_deactive_date()))== false)){					$info["isActive"] = 1;				}else{					//..check the deactive date is greater than todays date					if(strtotime($obj_tbl_rest_menu_opt->gettbl_rest_menu_opt_deactive_date()) > strtotime(date(DATE_FORMAT))){						$info["isActive"] = 1;					}				}			}		unset($obj_tbl_rest_menu_opt);		return $info;		}	}//..End GetInfo	public static function GetFields($data){		global $tbl_rest_menu_opt_active_condition;		$arr = array();		if(is_not_empty($data)){			$qry ="SELECT ".$data['key_field'].",".$data['value_field']." FROM ".TBL_REST_MENU_OPT."";			if(is_gt_zero_num($data['isActive'])){				$qry .= " WHERE ".$tbl_rest_menu_opt_active_condition;			}			$res = mysql_query($qry); 			if($res){				while($row=mysql_fetch_assoc($res)){					$arr[$row[$data['key_field']]] = $row[$data['value_field']];				}			}		}		return $arr;	}//.. End of GetFields		public static function getRewardTypes(){		return array('CUSTOMER'=>'CUSTOMER','RESTAURANT'=>'RESTAURANT');	}	}//..End tbl_rest_menu_opt?>