pired($rl_fn_order_expired);				$this->setrl_fn_promotion_live($rl_fn_promotion_live);				$this->setrl_fn_promotion_expired($rl_fn_promotion_expired);				$this->setrl_fn_payment_process($rl_fn_payment_process);				$this->setrl_fn_start_date(date(DATE_FORMAT));				$this->insert();				return $this->getrl_fn_id();			}		}		return OPERATION_FAIL;	}//..create	public function update($rl_fn_id, $rl_fn_role, $rl_fn_tablests_live, $rl_fn_tablests_expired, $rl_fn_request_live, $rl_fn_request_expired, $rl_fn_order_live, $rl_fn_order_expired, $rl_fn_promotion_live, $rl_fn_promotion_expired, $rl_fn_payment_process, $rl_fn_start_date, $rl_fn_end_date) {		if(is_gt_zero_num($rl_fn_id)){			if ($this->readObject(array(RL_FN_ID=>$rl_fn_id))){				$this->setrl_fn_role($rl_fn_role);				$this->setrl_fn_tablests_live($rl_fn_tablests_live);				$this->setrl_fn_tablests_expired($rl_fn_tablests_expired);				$this->setrl_fn_request_live($rl_fn_request_live);				$this->setrl_fn_request_expired($rl_fn_request_expired);				$this->setrl_fn_order_live($rl_fn_order_live);				$this->setrl_fn_order_expired($rl_fn_order_expired);				$this->setrl_fn_promotion_live($rl_fn_promotion_live);				$this->setrl_fn_promotion_expired($rl_fn_promotion_expired);				$this->setrl_fn_payment_process($rl_fn_payment_process);				$this->insert();				return OPERATION_SUCCESS;			}		}		return OPERATION_FAIL;	}//..update	public function activate($rl_fn_id){		if(is_gt_zero_num($rl_fn_id)){			if ($this->readObject(array(RL_FN_ID=>$rl_fn_id))){				$qry  = "UPDATE ".TBL_ROLE_FUNCTIONALITY.RET."SET".RET."			".TBL_ROLE_FUNCTIONALITY_DEACTIVE_DATE." = '".date(EMPTY_DATE_FORMAT)."' WHERE ".RL_FN_ID."={$rl_fn_id}";				$res = mysql_query($qry);				if($res){					return 1;				}			}		}		return 0;	}//..end activate	public function deactivate($rl_fn_id){		if(is_gt_zero_num($rl_fn_id)){			if ($this->readObject(array(RL_FN_ID=>$rl_fn_id))){				$qry  = "UPDATE ".TBL_ROLE_FUNCTIONALITY.RET."SET".RET."			".TBL_ROLE_FUNCTIONALITY_DEACTIVE_DATE." = '".date(DATE_FORMAT)."' WHERE ".RL_FN_ID."={$rl_fn_id}";				$res = mysql_query($qry);				if($res){					return 1;				}			}		}		return 0;	}//..end deactivate	public static function GetInfo($rl_fn_id=0,$rl_fn_role=0) {		$arr = array();		$info = array(); 		//..fectch based on the '$rl_fn_id' or role id '$rl_fn_id'		if(is_gt_zero_num($rl_fn_id)){			 $arr['rl_fn_id'] = $rl_fn_id;				}		if(is_gt_zero_num($rl_fn_role)){			 $arr['rl_fn_role'] = $rl_fn_role;				}				if(is_not_empty($arr)){ 			$obj_tbl_role_functionality = new tbl_role_functionality();			if($obj_tbl_role_functionality->readObject($arr)){				$info["rl_fn_id"]=$obj_tbl_role_functionality->getrl_fn_id();				$info["rl_fn_role"]=$obj_tbl_role_functionality->getrl_fn_role();								$info["rl_fn_tablests_live"]=$obj_tbl_role_functionality->getrl_fn_tablests_live();				$info["rl_fn_tablests_expired"]=$obj_tbl_role_functionality->getrl_fn_tablests_expired();								$info["rl_fn_request_live"]=$obj_tbl_role_functionality->getrl_fn_request_live();				$info["rl_fn_request_expired"]=$obj_tbl_role_functionality->getrl_fn_request_expired();				$info["rl_fn_order_live"]=$obj_tbl_role_functionality->getrl_fn_order_live();				$info["rl_fn_order_expired"]=$obj_tbl_role_functionality->getrl_fn_order_expired();				$info["rl_fn_promotion_live"]=$obj_tbl_role_functionality->getrl_fn_promotion_live();				$info["rl_fn_promotion_expired"]=$obj_tbl_role_functionality->getrl_fn_promotion_expired();				$info["rl_fn_payment_process"]=$obj_tbl_role_functionality->getrl_fn_payment_process();				$info["rl_fn_start_date"]=$obj_tbl_role_functionality->getrl_fn_start_date();				$info["rl_fn_end_date"]=$obj_tbl_role_functionality->getrl_fn_end_date();				$info["member_role_name"]=$obj_tbl_role_functionality->member_role_name;				$info["isActive"] = 0;				//..check deactive date is not set or 0				if((is_not_empty($obj_tbl_role_functionality->gettbl_role_functionality_deactive_date())==false)  || (is_gt_zero_num(strtotime($obj_tbl_role_functionality->gettbl_role_functionality_deactive_date()))== false)){					$info["isActive"] = 1;				}else{					//..check the deactive date is greater than todays date					if(strtotime($obj_tbl_role_functionality->gettbl_role_functionality_deactive_date()) > strtotime(date(DATE_FORMAT))){						$info["isActive"] = 1;					}				}			}		unset($obj_tbl_role_functionality);		return $info;		}	}//..End GetInfo	public static function GetFields($data){		global $tbl_role_functionality_active_condition;		$arr = array();		if(is_not_empty($data)){			$qry ="SELECT ".$data['key_field'].",".$data['value_field']." FROM ".TBL_ROLE_FUNCTIONALITY."";			if(is_gt_zero_num($data['isActive'])){				$qry .= " WHERE ".$tbl_role_functionality_active_condition;			}			$res = mysql_query($qry); 			if($res){				while($row=mysql_fetch_assoc($res)){					$arr[$row[$data['key_field']]] = $row[$data['value_field']];				}			}		}		return $arr;	}//.. End of GetFields}//..End tbl_role_functionality?>