';			 //echo $qry;			 		 	 $res = mysql_query($qry);		  if($res){				$cnt = 0;				while($row = mysql_fetch_assoc($res)){ 				  $arr[$cnt][SRVC_REQ_STG_ID] 		= $row[SRVC_REQ_STG_ID];          $arr[$cnt]['actual_time']				= $row[SRVC_REQ_STG_TIME_FINISHED];          $arr[$cnt]['service_name'] 			= $row[STATUS_NAME];          $arr[$cnt]['expected_time']			= $row[STATUS_EXP_TIME];					$arr[$cnt]['diff_time'] 				= $arr[$cnt]['actual_time'] - $arr[$cnt]['expected_time']; 					$arr[$cnt]['diff_time_min'] 		= biz_convSecToMin($arr[$cnt]['diff_time']);				 	$arr[$cnt]['expected_time_min'] = biz_convSecToMin($arr[$cnt]['expected_time']);				 	$arr[$cnt]['actual_time_min'] 	= biz_convSecToMin($arr[$cnt]['actual_time']);				 	$cnt++;				}			}		 }			 return $arr;	}	 }//..End tbl_service_request_stage?>