rvice_id){ 		$info = 0;		global $tbl_service_stage_active_condition; 		if($srvc_stg_service_id != ""){			 $qry = "SELECT MAX(".SRVC_STG_ID.") FROM ".TBL_SERVICE_STAGE." WHERE ".SRVC_STG_SERVICE_ID." = ".$srvc_stg_service_id." AND".$tbl_service_stage_active_condition; 			 $res = mysql_query($qry); 			 if($res)			 	return mysql_result($res,0);		}		return $info;	}	 		private static function getDfltStages(){		$_array = array();		$_array[0] = array('title'=>'Initiated','desc'=>'Service Initiated by customer/Auto','time'=>0,'order'=>1);		$_array[1] = array('title'=>'Picked','desc'=>'Service Picked By the Waiter','time'=>120,'order'=>2);		$_array[2] = array('title'=>'Completed','desc'=>'Service Completed By the Waiter','time'=>300,'order'=>3);		return $_array;	} 		public static function createDefaultStages($service_id){		if(is_gt_zero_num($service_id)){		$stages_array = self::getDfltStages(); 		if(is_not_empty($stages_array)){			$values = array();			foreach($stages_array as  $stage){				$values[] = '('.$service_id.', \''.$stage['title'].'\', \''.$stage['desc'].'\', '.$stage['time'].', '.$stage['order'].', \''.date(DATE_FORMAT).'\')';			} 			if(is_not_empty($values)){			$res =	mysql_query('INSERT INTO `'.TBL_SERVICE_STAGE.'` (`srvc_stg_service_id`, `srvc_stg_name`, `srvc_stg_description`, `srvc_stg_thresh_hold_time`, `srvc_stg_sort_order`, `srvc_stg_start_time`) VALUES'.biz_implode(',', $values)); 				if($res){					return OPERATION_SUCCESS;				} 			} 		}	}	 	return OPERATION_FAIL;	 	}}//..End tbl_service_stage?>