imit ,$srvc_start_date ,$srvc_end_date) {		if(is_not_empty($srvc_name)){			if($this->isAlreadyThere($srvc_name,$srvc_cat_id ,$srvc_description ,$srvc_by_restrnt_or_cust ,$srvc_attend_time_limit ,$srvc_provide_time_limit ,$srvc_start_date ,$srvc_end_date)){				return -1;			}else{				$this->setsrvc_id("");				$this->setsrvc_name($srvc_name);				$this->setsrvc_img($srvc_img);				$this->setsrvc_cat_id($srvc_cat_id);				$this->setsrvc_description($srvc_description);				$this->setsrvc_by_restrnt_or_cust($srvc_by_restrnt_or_cust);				$this->setsrvc_attend_time_limit($srvc_attend_time_limit);				$this->setsrvc_provide_time_limit($srvc_provide_time_limit);				$this->setsrvc_start_date(date(DATE_FORMAT));				//$this->setsrvc_end_date($srvc_end_date);				$this->insert();				//..on new service request create we need to add one satge as initiated				//..get last insert service ID				$srvc_stg_service_id=$this->getsrvc_id();				tbl_service_stage::createDefaultStages($srvc_stg_service_id);								//changed @20Aug2013#1				/*$srvc_stg_name='Initiated';				$srvc_stg_description='Service Initiated by customer';				$srvc_stg_thresh_hold_time=0;				$srvc_stg_sort_order=1;				$srvc_stg_start_time=Date(DATE_FORMAT);				$srvc_stg_end_time=NULL;				$objtbl_service_stage=new tbl_service_stage();				$objtbl_service_stage->create($srvc_stg_service_id ,$srvc_stg_name ,$srvc_stg_description ,$srvc_stg_thresh_hold_time ,$srvc_stg_sort_order ,$srvc_stg_start_time ,$srvc_stg_end_time);				unset($objtbl_service_stage);*/				return $srvc_stg_service_id;			}		}	}//..create	public function update($srvc_id, $srvc_name,$srvc_img, $srvc_cat_id, $srvc_description, $srvc_by_restrnt_or_cust, $srvc_attend_time_limit, $srvc_provide_time_limit, $srvc_start_date, $srvc_end_date) {		if(is_gt_zero_num($srvc_id)){			if ($this->readObject(array(SRVC_ID=>$srvc_id))){				$this->setsrvc_name($srvc_name);				if(is_not_empty($srvc_img)){					$this->setsrvc_img($srvc_img);				}								$this->setsrvc_cat_id($srvc_cat_id);				$this->setsrvc_description($srvc_description); 				$this->setsrvc_by_restrnt_or_cust($srvc_by_restrnt_or_cust);				$this->setsrvc_attend_time_limit($srvc_attend_time_limit);				$this->setsrvc_provide_time_limit($srvc_provide_time_limit);				//$this->setsrvc_start_date($srvc_start_date);				//$this->setsrvc_end_date($srvc_end_date);				//echo "srvc_img=$srvc_img";				$this->insert();				return 1;			}		}		return 0;	}//..update	public function activate($srvc_id){		if(is_not_empty($srvc_id)){			//if ($this->readObject(array(SRVC_ID=>$srvc_id))){				$qry  = "UPDATE ".TBL_SERVICES_CODE.RET."SET".RET."			".TBL_SERVICES_CODE_DEACTIVE_DATE." = '".date(EMPTY_DATE_FORMAT)."' WHERE ".SRVC_ID." IN ({$srvc_id})";			//echo $qry; 				$res = mysql_query($qry);				if($res){					return 1;				}			//}		}		return 0;	}//..end activate	public function deactivate($srvc_id){		if(is_not_empty($srvc_id)){			//if ($this->readObject(array(SRVC_ID=>$srvc_id))){				$qry  = "UPDATE ".TBL_SERVICES_CODE.RET."SET".RET."				".TBL_SERVICES_CODE_DEACTIVE_DATE." = '".date(DATE_FORMAT)."' WHERE ".SRVC_ID." IN ({$srvc_id})";				//echo $qry; 				$res = mysql_query($qry);				if($res){					return 1;				}			//}		}		return 0;	}//..end deactivate	public static function GetInfo($srvc_id) {		$info = array();		if($srvc_id != ""){			$obj = new tbl_services_code();			if($obj->readObject(array("srvc_id"=>$srvc_id))){				$info["srvc_id"]=$obj->getsrvc_id();				$info["srvc_name"]=$obj->getsrvc_name();				$info["srvc_img"]=$obj->getsrvc_img();								$info["srvc_cat_id"]=$obj->getsrvc_cat_id();				$info["srvc_description"]=$obj->getsrvc_description();				$info["srvc_by_restrnt_or_cust"]=$obj->getsrvc_by_restrnt_or_cust();				$info["srvc_attend_time_limit"]=$obj->getsrvc_attend_time_limit();				$info["srvc_provide_time_limit"]=$obj->getsrvc_provide_time_limit();				$info["srvc_start_date"]=$obj->getsrvc_start_date();				$info["srvc_end_date"]=$obj->getsrvc_end_date();								$info["srvc_restaurent"]=$obj->getsrvc_restaurent();								$info["isActive"] = chk_if_record_active($info["srvc_end_date"]);												 				//..check deactive date is not set or 0				/*if(is_not_empty($obj->gettbl_services_code_deactive_date())==false  || strtotime($obj->gettbl_services_code_deactive_date()) == 0){					$info["isActive"] = 1;				}else{					//..check the deactive date is greater than todays date					if(strtotime($obj->gettbl_services_code_deactive_date()) >= strtotime(date("Y-m-d h:i:s"))){						$info["isActive"] = 1;					}				}*/								 $info['id'] =$obj->getsrvc_id(); 				 $info['name']=$obj->getsrvc_name();				 $info['image']=$obj->getsrvc_img();				 				 $info['description']=$obj->getsrvc_description();				 $info['restrnt_or_cust']=$obj->getsrvc_by_restrnt_or_cust();				 if($obj->getsrvc_by_restrnt_or_cust() == "CUST"){				 	$info['by'] = $_lang['services_code']['label']["srvc_by_cust"];				 }else{				 	$info['by'] = $info['by'] = $_lang['services_code']['label']["srvc_by_restrnt"];				 }				 				 $info['start_date']=$obj->getsrvc_start_date();				 $info['end_date']= $obj->getsrvc_end_date();				 $info["restaurent"]=$obj->getsrvc_restaurent();				 $info['friendly_start_date']=friendly_time(strtotime($obj->getsrvc_start_date()));				 $info['friendly_end_date']=friendly_time(strtotime($obj->getsrvc_end_date()));			}		 unset($obj);		return $info;		}	}//..End GetInfo		public function GetAll($url="",$offset=0,$limit=10){	 	$info = array();  	    $results_count =0;		$array = array("offset"=>$offset,"limit"=>$limit); 		$services = $this->readArray( $array, $results_count,1);   		if(is_gt_zero_num($results_count)){            $info['pagination'] = biz_pagination(		   							array(										"offset_word" =>"offset", 										"url"=>$url,										"limit"=>$limit,										"offset"=>$offset,										"count"=>$results_count 										 									)								); 		   $info['services'] = $services; 		}		 		$info['result_count'] = $results_count;	return $info;	} 		public function GetActiveServices(){		global $tbl_services_code_active_condition;		$arr = array();		$res = mysql_query("SELECT srvc_id,srvc_name FROM ".TBL_SERVICES_CODE." WHERE (".SRVC_RESTAURENT."=".$_SESSION[SES_RESTAURANT]." OR ".SRVC_RESTAURENT."=".STS_DFLT_RESTAURANT.") AND ".$tbl_services_code_active_condition);		//$res = mysql_query("SELECT srvc_id,srvc_name FROM ".TBL_SERVICES_CODE." WHERE ".$tbl_services_code_active_condition);	 		if($res){			while($row=mysql_fetch_assoc($res)){				$arr[$row['srvc_id']] = $row['srvc_name'];			}		}		return $arr;	}		public static function deletePrevSrvcImg($srvc_id,$save_path="") {		if(is_gt_zero_num($srvc_id)){			$tbl_services_code = new tbl_services_code();			if ($tbl_services_code->readObject(array(MENU_ID=>$srvc_id))){							$srvc_img=$tbl_services_code->getsrvc_img();				if(is_not_empty($srvc_img))					unlink($save_path.$srvc_img);			}		}			}//..update 		}//..End tbl_services_code?>