T 1');			 		 	 	   }		    	   return 0;	}		public static function isPendingCustomerForTables(){		$res = mysql_query('SELECT count('.TBL_CUST_SESS_ID.') FROM (SELECT * FROM '.TBL_TABLE_CUSTOMER_SESSION.'  ORDER BY  '.TBL_CUST_SESS_ID.' DESC, '.TBL_CUST_SESS_TABLE_ID.' ) t GROUP BY  '.TBL_CUST_SESS_TABLE_ID.' HAVING TBL_CUST_SESS_CUSTOMER ="" AND '.chkTableInRestaurant(TBL_CUST_SESS_TABLE_ID).'');	 		if($res && mysql_num_rows($res))			return mysql_result($res,0); 		return 0;	}		public static function deactivatePrevTblRecords($table_id){	global $tbl_table_customer_session_active_condition;	  if(is_gt_zero_num($table_id)){	  	 $res = mysql_query('UPDATE '.TBL_TABLE_CUSTOMER_SESSION.' SET '.TBL_CUST_SESS_END_DATE.' = "'.date(DATE_FORMAT).'" WHERE '.TBL_CUST_SESS_TABLE_ID.' = '.$table_id.' AND '.chkTableInRestaurant(TBL_CUST_SESS_TABLE_ID).' AND '.$tbl_table_customer_session_active_condition);		 return $res;	  }	  return 0;	} 		public static function getActiveTblSession($tbl_id){		global $tbl_table_customer_session_active_condition;		$res = mysql_query('SELECT  MAX('.TBL_CUST_SESS_ID.') FROM  '.TBL_TABLE_CUSTOMER_SESSION.' WHERE '.TBL_CUST_SESS_TABLE_ID.'='.$tbl_id.' AND ('.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.' is NULL OR '.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.'  = 0 OR TIME('.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.') > CURTIME( )) AND '.$tbl_table_customer_session_active_condition);		//echo 'SELECT  MAX('.TBL_CUST_SESS_ID.') FROM  '.TBL_TABLE_CUSTOMER_SESSION.' WHERE '.TBL_CUST_SESS_TABLE_ID.'='.$tbl_id.'  ('.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.' is NULL OR '.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.'  = 0 OR TIME('.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.') > CURTIME( )) AND '.$tbl_table_customer_session_active_condition;		if($res && mysql_num_rows($res)){			return mysql_result($res,0);		}		return 0;	}		public static function getActiveSessionForAlltables($isArray=0){	 global $tbl_table_customer_session_active_condition;  	 	$info = array();		/*echo ' SELECT '.tbl_cust_sess_id.'  FROM '.TBL_TABLE_CUSTOMER_SESSION.' WHERE  ('.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.' is NULL OR '.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.' = 0 OR TIME('.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.') > CURTIME( )) GROUP BY '.TBL_CUST_SESS_TABLE_ID;*/		/*$res = mysql_query(' SELECT MAX('.tbl_cust_sess_id.')  FROM '.TBL_TABLE_CUSTOMER_SESSION.' WHERE  ('.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.' is NULL OR '.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.' = 0 OR TIME('.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.') > CURTIME( )) GROUP BY '.TBL_CUST_SESS_TABLE_ID);*/		/*echo 'SELECT  '.TBL_CUST_SESS_TABLE_ID.',MAX('.TBL_CUST_SESS_ID.') FROM  '.TBL_TABLE_CUSTOMER_SESSION.' WHERE  ('.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.' is NULL OR '.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.'  = 0 OR TIME('.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.') > CURTIME( )) AND '.$tbl_table_customer_session_active_condition.' GROUP BY '.TBL_CUST_SESS_TABLE_ID.'';*/		$res = mysql_query('SELECT  '.TBL_CUST_SESS_TABLE_ID.',MAX('.TBL_CUST_SESS_ID.') FROM  '.TBL_TABLE_CUSTOMER_SESSION.' WHERE  ('.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.' is NULL OR '.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.'  = 0 OR TIME('.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.') > CURTIME( )) AND '.$tbl_table_customer_session_active_condition.' AND '.chkTableInRestaurant(TBL_CUST_SESS_TABLE_ID).' GROUP BY '.TBL_CUST_SESS_TABLE_ID.'');		 		if($res && mysql_num_rows($res)){			while($row = mysql_fetch_row($res)){				$info[$row[0]] = $row[1];			} 		}				if(is_not_empty($info)){			if(is_gt_zero_num($isArray)){				return $info;			}else{				return implode(",",$info);			} 		}		return '';	}		public static function is_in_live_session($session){		$active_session = self::getActiveSessionForAlltables();	  return in_array($session, biz_explode(',',$active_session));			 	} 		/**	* Return list of Live sess tables  	*/	public static function getLiveSessTables($_all_details=false){   		global $tbl_table_customer_session_active_condition;		$sql='		SELECT  '.TBL_CUST_SESS_TABLE_ID.','.TABLE_NUMBER.','.TABLE_POS_X.','.TABLE_POS_Y.','.TABLE_DWG_WIDTH.','.TABLE_DWG_HEIGHT.','.TABLE_SEATING_CAPACITY.','.TBL_CUST_SESS_PARTY_SIZE.', '.TBL_CUST_SESS_ID.'				FROM  		'.TBL_TABLE_CUSTOMER_SESSION.' 		INNER JOIN '.TBL_DINING_TABLE.' 		ON '.TBL_CUST_SESS_TABLE_ID.'='.TABLE_ID.' 		WHERE  		('.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.' is NULL		 OR '.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.'  = 0 		 OR TIME('.TBL_TABLE_CUSTOMER_SESSION_DEACTIVE_DATE.') > CURTIME( )) 		 AND '.$tbl_table_customer_session_active_condition.'		 AND '.chkTableInRestaurant(TBL_CUST_SESS_TABLE_ID).'		 AND '.TBL_CUST_SESS_ID.' IN (		 SELECT MAX('.TBL_CUST_SESS_ID.') 		 FROM '.TBL_TABLE_CUSTOMER_SESSION.' 		 GROUP BY '.TBL_CUST_SESS_TABLE_ID.');';		$res = mysql_query($sql);		//echo $sql;					 if($res){		 	 while($row= mysql_fetch_assoc($res)){			 					if($_all_details==TRUE){					$arr[$row[TBL_CUST_SESS_TABLE_ID]] = $row;				}else{					$arr[$row[TBL_CUST_SESS_TABLE_ID]] = $row[TABLE_NUMBER];				}			 }		 }		 return $arr;  	}	 }//..End tbl_table_customer_session?>