>getord_dtl_price();				$info["ord_dtl_discount"]=$obj_tbl_tmp_order_prom->getord_dtl_discount();				$info["ordprom_promotion"]=$obj_tbl_tmp_order_prom->getordprom_promotion();				$info["ordprom_discount_id"]=$obj_tbl_tmp_order_prom->getordprom_discount_id();				$info["ordprom_discount_amt"]=$obj_tbl_tmp_order_prom->getordprom_discount_amt();				$info["ord_owner_id"]=$obj_tbl_tmp_order_prom->getord_owner_id();				$info["isActive"] = 0;				//..check deactive date is not set or 0				if((is_not_empty($obj_tbl_tmp_order_prom->gettbl_tmp_order_prom_deactive_date())==false)  || (is_gt_zero_num(strtotime($obj_tbl_tmp_order_prom->gettbl_tmp_order_prom_deactive_date()))== false)){					$info["isActive"] = 1;				}else{					//..check the deactive date is greater than todays date					if(strtotime($obj_tbl_tmp_order_prom->gettbl_tmp_order_prom_deactive_date()) > strtotime(date(DATE_FORMAT))){						$info["isActive"] = 1;					}				}			}		unset($obj_tbl_tmp_order_prom);		return $info;		}	}//..End GetInfo	public static function GetFields($data){		global $tbl_tmp_order_prom_active_condition;		$arr = array();		if(is_not_empty($data)){			$qry ="SELECT ".$data['key_field'].",".$data['value_field']." FROM ".TBL_TMP_ORDER_PROM."";			if(is_gt_zero_num($data['isActive'])){				$qry .= " WHERE ".$tbl_tmp_order_prom_active_condition;			}			$res = mysql_query($qry); 			if($res){				while($row=mysql_fetch_assoc($res)){					$arr[$row[$data['key_field']]] = $row[$data['value_field']];				}			}		}		return $arr;	}//.. End of GetFields}//..End tbl_tmp_order_prom?>