<?phpinclude_once(dirname(dirname(__FILE__)).'/init.php');include_once('header.php');$order_id 			= get_input('order_id',0); $order_tip 			= get_input('order_tip',0);$payment_choice	= strtoupper(get_input('payment_choice','INDIVIDUAL'));  $is_pay_now = 0;if(is_gt_zero_num($order_id)){		$tbl_ordersinfo = tbl_orders::GetInfo($order_id);	if($payment_choice == 'INDIVIDUAL'){		$all_ses_ord_dtl = tbl_orders::getAllOrderDetailBySession($tbl_ordersinfo[ORDER_SESSION_ID],$order_id);		 	}else{		$all_ses_ord_dtl = tbl_orders::getAllOrderDetailBySession($tbl_ordersinfo[ORDER_SESSION_ID]);	}  //print_r($all_ses_ord_dtl); if(is_gt_zero_num($order_tip)){ 	 $is_pay_now = 1; } if((is_gt_zero_num($order_tip)) && (is_gt_zero_num($all_ses_ord_dtl['amounts']['tip_amt'])==false)){ 	  	$all_ses_ord_dtl['amounts']['tip_amt'] = $order_tip; 	$all_ses_ord_dtl['amounts']['gr_amt'] =  $all_ses_ord_dtl['amounts']['gr_amt']  + $order_tip; }  	$smarty->assign('tbl_ordersinfo',$tbl_ordersinfo); 	$smarty->assign('all_ses_ord_dtl',$all_ses_ord_dtl);	$smarty->assign('order_tip',$order_tip);	$smarty->assign('isMenuActive',0);	$smarty->assign('payment_choice',$payment_choice);	//$smarty->assign('is_pay_now',$is_pay_now); 	$template = 'order_receipt.tpl';  //tbl_orders::_validate_order_to_ethor_pos($order_id);   }else{	$template = 'index.tpl';}include_once('footer.php');?>