f(is_gt_zero_num($isSuccess)){		$err .= "<div class='info'>".$_lang['tbl_admin_dashboard'][$action]['SUCCESS_MSG']."</div>";	}elseif($isSuccess == OPERATION_FAIL){		$err .= "<div class='error'>".$_lang['tbl_admin_dashboard'][$action]['FAILURE_MSG']."</div>";	}elseif($isSuccess == OPERATION_DUPLICATE){		$err .= "<div class='error'>".$_lang['tbl_admin_dashboard'][$action]['DUPLICATE_MSG']."</div>";	}}//..if	$result_found=0;	$tbl_admin_dashboardlist = $objtbl_admin_dashboard->readArray(array(OFFSET_TITLE=>$offset,LIMIT_TITLE=>$limit,SORT_BY=>$sort_by,SORT_ON=>$sort_on),$result_found,1);	$allpageCount = 0;	$currentPage = 0;	$smarty->assign('pagination',biz_pagination(array("url"=>$navigationURL,LIMIT_TITLE=>$limit,OFFSET_TITLE=>$offset,"count"=>$result_found),$allpageCount,$currentPage));	$smarty->assign('allpageCount',$allpageCount);	$smarty->assign('currentPage',$currentPage);	$smarty->assign('tbl_admin_dashboardlist', $tbl_admin_dashboardlist);	$smarty->assign('result_found',$result_found);	$template = "tbl_admin_dashboard/grid.tpl";	if (is_not_empty($mode)){		if(($mode == MODE_VIEW || $mode==MODE_UPDATE) && is_gt_zero_num($admdash_id)){			$tbl_admin_dashboardinfo= $objtbl_admin_dashboard->GetInfo($admdash_id);			$smarty->assign('tbl_admin_dashboardinfo',$tbl_admin_dashboardinfo);			if($mode==MODE_UPDATE){				$smarty->assign("isUpdate",1);			}			$template = "tbl_admin_dashboard/view.tpl";		}elseif($mode == MODE_CREATE){			$template = "tbl_admin_dashboard/create.tpl";					}}	$smarty->assign('page_url', $url);	$smarty->assign('navigationURL',$navigationURL);	$smarty->assign('new_sort',$new_sort);	$smarty->assign('active_page',$active_page);/*}else{	$template="index.tpl";}*/include_once("footer.php");?>