<?phpinclude_once(dirname(dirname(__FILE__))."/init.php");include_once("header.php");/*if($sesslife==true){*/$customer_id= get_input("customer_id");$cutomer_name= get_input("cutomer_name");$cutomer_phone_no= get_input("cutomer_phone_no");$customer_email= get_input("customer_email");$customer_address= get_input("customer_address");$customer_city= get_input("customer_city");$customer_zip= get_input("customer_zip");$cutomer_created_on= get_input("cutomer_created_on");$customer_end_date= get_input("customer_end_date");$action = strtoupper(get_input(ACTION_TITLE));$mode = strtoupper(get_input(MODE_TITLE));$offset = get_input(OFFSET_TITLE,OFFSET_VALUE);$limit =  get_input(LIMIT_TITLE,LIMIT_VALUE);$url = "{$website}/user/tbl_customers.php";$isSuccess = "";$objtbl_customers= new tbl_customers();if(is_not_empty($action)){	switch($action){		case ACTION_CREATE: 			$isSuccess = $objtbl_customers->create($cutomer_name, $cutomer_phone_no, $customer_email, $customer_address, $customer_city, $customer_zip, $cutomer_created_on, $customer_end_date);			break;		case ACTION_UPDATE: 			$isSuccess = $objtbl_customers->update($customer_id, $cutomer_name, $cutomer_phone_no, $customer_email, $customer_address, $customer_city, $customer_zip, $cutomer_created_on, $customer_end_date);			break;		case ACTION_DELETE: 			$isSuccess = $objtbl_customers->delete(array(CUSTOMER_ID=>$customer_id));			break;		case ACTION_ACTIVATE: 			$isSuccess = $objtbl_customers->activate($customer_id);			break;		case ACTION_DEACTIVATE: 			$isSuccess = $objtbl_customers->deactivate($customer_id);			break;	}//..switch}//..ifif(is_not_empty($isSuccess)){	if(is_gt_zero_num($isSuccess)){		$err .= "<div class='info'>".$_lang['tbl_customers'][$action]['SUCCESS_MSG']."</div>";	}elseif($isSuccess == OPERATION_FAIL){		$err .= "<div class='error'>".$_lang['tbl_customers'][$action]['FAILURE_MSG']."</div>";	}elseif($isSuccess == OPERATION_DUPLICATE){		$err .= "<div class='error'>".$_lang['tbl_customers'][$action]['DUPLICATE_MSG']."</div>";	}}//..if	$result_found=0;	$tbl_customerslist = $objtbl_customers->readArray(array(OFFSET_TITLE=>$offset,LIMIT_TITLE=>$limit),$result_found,1);	$smarty->assign('pagination',biz_pagination(array("url"=>$url,LIMIT_TITLE=>$limit,OFFSET_TITLE=>$offset,"count"=>$result_found)));	$smarty->assign('tbl_customerslist', $tbl_customerslist);	$smarty->assign('result_found',$result_found);	$template = "tbl_customers/tbl_customers.tpl";	if (is_not_empty($mode)){		if(($mode == MODE_VIEW || $mode==MODE_UPDATE) && is_gt_zero_num($customer_id)){			$tbl_customersinfo= $objtbl_customers->GetInfo($customer_id);			$smarty->assign('tbl_customersinfo',$tbl_customersinfo);			if($mode==MODE_UPDATE){				$smarty->assign("isUpdate",1);			}			$template = "tbl_customers/view.tpl";		}elseif($mode == MODE_CREATE){			$template = "tbl_customers/create.tpl";					}}	$smarty->assign('page_url', $url);/*}else{	$template="index.tpl";}*/include_once("footer.php");?>