<?phpinclude_once(dirname(dirname(__FILE__))."/init.php");include_once("header.php");$active_page = "tbl_food_notes";if($sesslife==true){$fdnote_id= get_input("fdnote_id");$fdnote_number= get_sql_input("fdnote_number");$fdnote_desc= get_sql_input("fdnote_desc");$fdnote_restarant= get_input("fdnote_restarant",$_SESSION[SES_RESTAURANT]);$fdnote_start_date= get_input("fdnote_start_date");$fdnote_end_date= get_input("fdnote_end_date");$action = strtoupper(get_input(ACTION_TITLE));$mode = strtoupper(get_input(MODE_TITLE));$offset = get_input(OFFSET_TITLE,OFFSET_VALUE);$limit =  get_input(LIMIT_TITLE,LIMIT_VALUE);$sort_on = get_input(SORT_ON,"fdnote_id");$sort_by=$new_sort="";biz_set_sorting_var($sort_by,$new_sort);$url = "{$website}/user/tbl_food_notes.php";$navigationURL = $url."?".SORT_ON."={$sort_on}&".SORT_BY."={$sort_by}";$isSuccess = "";$sel_tbl_food_notes = get_input('sel_tbl_food_notes');if($sel_tbl_food_notes){ 		$sel_tbl_food_notes_ids = biz_implode(',',array_keys($sel_tbl_food_notes)); }$objtbl_food_notes= new tbl_food_notes();//echo "action=$action";if(is_not_empty($action)){	switch($action){ 		case ACTION_CREATE: 			$isSuccess = $objtbl_food_notes->create($fdnote_number, $fdnote_desc, $fdnote_start_date, $fdnote_end_date,$fdnote_restarant);			break;					case ACTION_UPDATE: 			$isSuccess = $objtbl_food_notes->update($fdnote_id, $fdnote_number, $fdnote_desc, $fdnote_start_date, $fdnote_end_date,$fdnote_restarant);			break;					case ACTION_DELETE: 			//$isSuccess = $objtbl_food_notes->delete(array(FDNOTE_ID=>$fdnote_id));			if(is_not_empty($sel_tbl_food_notes_ids)){ 		 	 $isSuccess = $objtbl_food_notes->delete(array(FDNOTE_ID=>$sel_tbl_food_notes_ids));		 }else{		 	 $isSuccess = $objtbl_food_notes->delete(array(FDNOTE_ID=>$fdnote_id));		 }						break;						case ACTION_ACTIVATE:			if(is_not_empty($sel_tbl_food_notes_ids)){ 			 $isSuccess = $objtbl_food_notes->activate($sel_tbl_food_notes_ids);		 }else{		 	 $isSuccess = $objtbl_food_notes->activate($fdnote_id);		 } 		 			break;					case ACTION_DEACTIVATE: 			if(is_not_empty($sel_tbl_food_notes_ids)){ 				$isSuccess = $objtbl_food_notes->deactivate($sel_tbl_food_notes_ids);		 }else{		 	 $isSuccess = $objtbl_food_notes->deactivate($fdnote_id);		 }  			break;							/*case ACTION_ACTIVATE: 			$isSuccess = $objtbl_food_notes->activate($fdnote_id);			break;		case ACTION_DEACTIVATE: 			$isSuccess = $objtbl_food_notes->deactivate($fdnote_id);			break;*/	}//..switch}//..ifif(is_not_empty($isSuccess)){	if(is_gt_zero_num($isSuccess)){		$err .= "<div class='info'>".$_lang['tbl_food_notes'][$action]['SUCCESS_MSG']."</div>";	}elseif($isSuccess == OPERATION_FAIL){		$err .= "<div class='error'>".$_lang['tbl_food_notes'][$action]['FAILURE_MSG']."</div>";	}elseif($isSuccess == OPERATION_DUPLICATE){		$err .= "<div class='error'>".$_lang['tbl_food_notes'][$action]['DUPLICATE_MSG']."</div>";	}}//..if	$result_found=0;	$tbl_food_noteslist = $objtbl_food_notes->readArray(array(OFFSET_TITLE=>$offset,LIMIT_TITLE=>$limit,SORT_BY=>$sort_by,SORT_ON=>$sort_on),$result_found,1);	$allpageCount = 0;	$currentPage = 0;	$smarty->assign('pagination',biz_pagination(array("url"=>$navigationURL,LIMIT_TITLE=>$limit,OFFSET_TITLE=>$offset,"count"=>$result_found),$allpageCount,$currentPage));	$smarty->assign('allpageCount',$allpageCount);	$smarty->assign('currentPage',$currentPage);	$smarty->assign('tbl_food_noteslist', $tbl_food_noteslist);	$smarty->assign('result_found',$result_found);	$template = "tbl_food_notes/tbl_food_notes.tpl";	if (is_not_empty($mode)){		if(($mode == MODE_VIEW || $mode==MODE_UPDATE) && is_gt_zero_num($fdnote_id)){			$tbl_food_notesinfo= $objtbl_food_notes->GetInfo($fdnote_id);			$smarty->assign('tbl_food_notesinfo',$tbl_food_notesinfo);			if($mode==MODE_UPDATE){				$smarty->assign("isUpdate",1);			}			$template = "tbl_food_notes/view.tpl";		}elseif($mode == MODE_CREATE){			$template = "tbl_food_notes/create.tpl";					}}	$smarty->assign('page_url', $url);	$smarty->assign('navigationURL',$navigationURL);	$smarty->assign('new_sort',$new_sort);	$smarty->assign('active_page',$active_page);}else{	$template="index.tpl";}include_once("footer.php");?>