found=0;	$tbl_menu_categorylist = $objtbl_menu_category->readArray(array(MNU_CAT_RESTAURENT=>$_SESSION[SES_RESTAURANT],OFFSET_TITLE=>$offset,LIMIT_TITLE=>$limit,SORT_BY=>$sort_by,SORT_ON=>$sort_on),$result_found,1);	$allpageCount = 0;	$currentPage = 0;	$smarty->assign('pagination',biz_pagination(array("url"=>$navigationURL,LIMIT_TITLE=>$limit,OFFSET_TITLE=>$offset,"count"=>$result_found),$allpageCount,$currentPage));	$smarty->assign('allpageCount',$allpageCount);	$smarty->assign('currentPage',$currentPage);	$smarty->assign('tbl_menu_categorylist', $tbl_menu_categorylist);	$smarty->assign('result_found',$result_found);	$template = "tbl_menu_category/tbl_menu_category.tpl";	if (is_not_empty($mode)){		if(($mode == MODE_VIEW || $mode==MODE_UPDATE) && is_gt_zero_num($mnu_cat_id)){			$tbl_menu_categoryinfo= $objtbl_menu_category->GetInfo($mnu_cat_id);			$smarty->assign('tbl_menu_categoryinfo',$tbl_menu_categoryinfo);			if($mode==MODE_UPDATE){				$smarty->assign("isUpdate",1);			}			$template = "tbl_menu_category/view.tpl";		}elseif($mode == MODE_CREATE){			$template = "tbl_menu_category/create.tpl";					}}	$smarty->assign('page_url', $url);	$smarty->assign('navigationURL',$navigationURL);	$smarty->assign('new_sort',$new_sort);	$smarty->assign('active_page',$active_page);/*}else{	$template="index.tpl";}*/include_once("footer.php");?>