<?phpinclude_once(dirname(dirname(__FILE__))."/init.php");include_once("header.php");$active_page = "tbl_mnu_weekdays";/*if($sesslife==true){*/$mnu_wkdy_id= get_input("mnu_wkdy_id");$mnu_wkdy_menu_id= get_input("mnu_wkdy_menu_id");$mnu_wkdy_sunday= get_input("mnu_wkdy_sunday");$mnu_wkdy_monday= get_input("mnu_wkdy_monday");$mnu_wkdy_tuesday= get_input("mnu_wkdy_tuesday");$mnu_wkdy_wednesday= get_input("mnu_wkdy_wednesday");$mnu_wkdy_thursday= get_input("mnu_wkdy_thursday");$mnu_wkdy_friday= get_input("mnu_wkdy_friday");$mnu_wkdy_saturday= get_input("mnu_wkdy_saturday");$action = strtoupper(get_input(ACTION_TITLE));$mode = strtoupper(get_input(MODE_TITLE));$offset = get_input(OFFSET_TITLE,OFFSET_VALUE);$limit =  get_input(LIMIT_TITLE,LIMIT_VALUE);$sort_on = get_input(SORT_ON,"mnu_wkdy_id");$sort_by=$new_sort="";biz_set_sorting_var($sort_by,$new_sort);$url = "{$website}/user/tbl_mnu_weekdays.php";$navigationURL = $url."?".SORT_ON."={$sort_on}&".SORT_BY."={$sort_by}";$isSuccess = "";$objtbl_mnu_weekdays= new tbl_mnu_weekdays();if(is_not_empty($action)){	switch($action){		case ACTION_CREATE: 			$isSuccess = $objtbl_mnu_weekdays->create($mnu_wkdy_menu_id, $mnu_wkdy_sunday, $mnu_wkdy_monday, $mnu_wkdy_tuesday, $mnu_wkdy_wednesday, $mnu_wkdy_thursday, $mnu_wkdy_friday, $mnu_wkdy_saturday);			break;		case ACTION_UPDATE: 			$isSuccess = $objtbl_mnu_weekdays->update($mnu_wkdy_id, $mnu_wkdy_menu_id, $mnu_wkdy_sunday, $mnu_wkdy_monday, $mnu_wkdy_tuesday, $mnu_wkdy_wednesday, $mnu_wkdy_thursday, $mnu_wkdy_friday, $mnu_wkdy_saturday);			break;		case ACTION_DELETE: 			$isSuccess = $objtbl_mnu_weekdays->delete(array(MNU_WKDY_ID=>$mnu_wkdy_id));			break;		case ACTION_ACTIVATE: 			$isSuccess = $objtbl_mnu_weekdays->activate($mnu_wkdy_id);			break;		case ACTION_DEACTIVATE: 			$isSuccess = $objtbl_mnu_weekdays->deactivate($mnu_wkdy_id);			break;	}//..switch}//..ifif(is_not_empty($isSuccess)){	if(is_gt_zero_num($isSuccess)){		$err .= "<div class='info'>".$_lang['tbl_mnu_weekdays'][$action]['SUCCESS_MSG']."</div>";	}elseif($isSuccess == OPERATION_FAIL){		$err .= "<div class='error'>".$_lang['tbl_mnu_weekdays'][$action]['FAILURE_MSG']."</div>";	}elseif($isSuccess == OPERATION_DUPLICATE){		$err .= "<div class='error'>".$_lang['tbl_mnu_weekdays'][$action]['DUPLICATE_MSG']."</div>";	}}//..if	$result_found=0;	$tbl_mnu_weekdayslist = $objtbl_mnu_weekdays->readArray(array(OFFSET_TITLE=>$offset,LIMIT_TITLE=>$limit,SORT_BY=>$sort_by,SORT_ON=>$sort_on),$result_found,1);	$allpageCount = 0;	$currentPage = 0;	$smarty->assign('pagination',biz_pagination(array("url"=>$navigationURL,LIMIT_TITLE=>$limit,OFFSET_TITLE=>$offset,"count"=>$result_found),$allpageCount,$currentPage));	$smarty->assign('allpageCount',$allpageCount);	$smarty->assign('currentPage',$currentPage);	$smarty->assign('tbl_mnu_weekdayslist', $tbl_mnu_weekdayslist);	$smarty->assign('result_found',$result_found);	$template = "tbl_mnu_weekdays/tbl_mnu_weekdays.tpl";	if (is_not_empty($mode)){		if(($mode == MODE_VIEW || $mode==MODE_UPDATE) && is_gt_zero_num($mnu_wkdy_id)){			$tbl_mnu_weekdaysinfo= $objtbl_mnu_weekdays->GetInfo($mnu_wkdy_id);			$smarty->assign('tbl_mnu_weekdaysinfo',$tbl_mnu_weekdaysinfo);			if($mode==MODE_UPDATE){				$smarty->assign("isUpdate",1);			}			$template = "tbl_mnu_weekdays/view.tpl";		}elseif($mode == MODE_CREATE){			$template = "tbl_mnu_weekdays/create.tpl";					}}	$smarty->assign('page_url', $url);	$smarty->assign('navigationURL',$navigationURL);	$smarty->assign('new_sort',$new_sort);	$smarty->assign('active_page',$active_page);/*}else{	$template="index.tpl";}*/include_once("footer.php");?>