<?phpinclude_once(dirname(dirname(__FILE__))."/init.php");include_once("header.php");$active_page = "tbl_order_details_options";/*if($sesslife==true){*/$ord_det_opt_id= get_input("ord_det_opt_id");$ord_det_opt_order_detail= get_input("ord_det_opt_order_detail");$ord_det_opt_optionvalue= get_input("ord_det_opt_optionvalue");$ord_det_opt_qty= get_input("ord_det_opt_qty");$ord_det_opt_price= get_input("ord_det_opt_price");$action = strtoupper(get_input(ACTION_TITLE));$mode = strtoupper(get_input(MODE_TITLE));$offset = get_input(OFFSET_TITLE,OFFSET_VALUE);$limit =  get_input(LIMIT_TITLE,LIMIT_VALUE);$sort_on = get_input(SORT_ON,"ord_det_opt_id");$sort_by=$new_sort="";biz_set_sorting_var($sort_by,$new_sort);$url = "{$website}/user/tbl_order_details_options.php";$navigationURL = $url."?".SORT_ON."={$sort_on}&".SORT_BY."={$sort_by}";$isSuccess = "";$objtbl_order_details_options= new tbl_order_details_options();if(is_not_empty($action)){	switch($action){		case ACTION_CREATE: 			$isSuccess = $objtbl_order_details_options->create($ord_det_opt_order_detail, $ord_det_opt_optionvalue, $ord_det_opt_qty, $ord_det_opt_price);			break;		case ACTION_UPDATE: 			$isSuccess = $objtbl_order_details_options->update($ord_det_opt_id, $ord_det_opt_order_detail, $ord_det_opt_optionvalue, $ord_det_opt_qty, $ord_det_opt_price);			break;		case ACTION_DELETE: 			$isSuccess = $objtbl_order_details_options->delete(array(ORD_DET_OPT_ID=>$ord_det_opt_id));			break;		case ACTION_ACTIVATE: 			$isSuccess = $objtbl_order_details_options->activate($ord_det_opt_id);			break;		case ACTION_DEACTIVATE: 			$isSuccess = $objtbl_order_details_options->deactivate($ord_det_opt_id);			break;	}//..switch}//..ifif(is_not_empty($isSuccess)){	if(is_gt_zero_num($isSuccess)){		$err .= "<div class='info'>".$_lang['tbl_order_details_options'][$action]['SUCCESS_MSG']."</div>";	}elseif($isSuccess == OPERATION_FAIL){		$err .= "<div class='error'>".$_lang['tbl_order_details_options'][$action]['FAILURE_MSG']."</div>";	}elseif($isSuccess == OPERATION_DUPLICATE){		$err .= "<div class='error'>".$_lang['tbl_order_details_options'][$action]['DUPLICATE_MSG']."</div>";	}}//..if	$result_found=0;	$tbl_order_details_optionslist = $objtbl_order_details_options->readArray(array(OFFSET_TITLE=>$offset,LIMIT_TITLE=>$limit,SORT_BY=>$sort_by,SORT_ON=>$sort_on),$result_found,1);	$allpageCount = 0;	$currentPage = 0;	$smarty->assign('pagination',biz_pagination(array("url"=>$navigationURL,LIMIT_TITLE=>$limit,OFFSET_TITLE=>$offset,"count"=>$result_found),$allpageCount,$currentPage));	$smarty->assign('allpageCount',$allpageCount);	$smarty->assign('currentPage',$currentPage);	$smarty->assign('tbl_order_details_optionslist', $tbl_order_details_optionslist);	$smarty->assign('result_found',$result_found);	$template = "tbl_order_details_options/tbl_order_details_options.tpl";	if (is_not_empty($mode)){		if(($mode == MODE_VIEW || $mode==MODE_UPDATE) && is_gt_zero_num($ord_det_opt_id)){			$tbl_order_details_optionsinfo= $objtbl_order_details_options->GetInfo($ord_det_opt_id);			$smarty->assign('tbl_order_details_optionsinfo',$tbl_order_details_optionsinfo);			if($mode==MODE_UPDATE){				$smarty->assign("isUpdate",1);			}			$template = "tbl_order_details_options/view.tpl";		}elseif($mode == MODE_CREATE){			$template = "tbl_order_details_options/create.tpl";					}}	$smarty->assign('page_url', $url);	$smarty->assign('navigationURL',$navigationURL);	$smarty->assign('new_sort',$new_sort);	$smarty->assign('active_page',$active_page);/*}else{	$template="index.tpl";}*/include_once("footer.php");?>