<?phpinclude_once(dirname(dirname(__FILE__))."/init.php");include_once("header.php");$active_page = "tbl_order_promotions";/*if($sesslife==true){*/$ordprom_id= get_input("ordprom_id");$ordprom_order= get_input("ordprom_order");$ordprom_order_sbmnu_dish= get_input("ordprom_order_sbmnu_dish");$ordprom_promotion= get_input("ordprom_promotion");$ordprom_discount= get_input("ordprom_discount");$ordprom_start_date= get_input("ordprom_start_date");$ordprom_end_date= get_input("ordprom_end_date");$action = strtoupper(get_input(ACTION_TITLE));$mode = strtoupper(get_input(MODE_TITLE));$offset = get_input(OFFSET_TITLE,OFFSET_VALUE);$limit =  get_input(LIMIT_TITLE,LIMIT_VALUE);$sort_on = get_input(SORT_ON,"ordprom_id");$sort_by=$new_sort="";biz_set_sorting_var($sort_by,$new_sort);$url = "{$website}/user/tbl_order_promotions.php";$navigationURL = $url."?".SORT_ON."={$sort_on}&".SORT_BY."={$sort_by}";$isSuccess = "";$objtbl_order_promotions= new tbl_order_promotions();if(is_not_empty($action)){	switch($action){		case ACTION_CREATE: 			$isSuccess = $objtbl_order_promotions->create($ordprom_order, $ordprom_order_sbmnu_dish, $ordprom_promotion, $ordprom_discount, $ordprom_start_date, $ordprom_end_date);			break;		case ACTION_UPDATE: 			$isSuccess = $objtbl_order_promotions->update($ordprom_id, $ordprom_order, $ordprom_order_sbmnu_dish, $ordprom_promotion, $ordprom_discount, $ordprom_start_date, $ordprom_end_date);			break;		case ACTION_DELETE: 			$isSuccess = $objtbl_order_promotions->delete(array(ORDPROM_ID=>$ordprom_id));			break;		case ACTION_ACTIVATE: 			$isSuccess = $objtbl_order_promotions->activate($ordprom_id);			break;		case ACTION_DEACTIVATE: 			$isSuccess = $objtbl_order_promotions->deactivate($ordprom_id);			break;	}//..switch}//..ifif(is_not_empty($isSuccess)){	if(is_gt_zero_num($isSuccess)){		$err .= "<div class='info'>".$_lang['tbl_order_promotions'][$action]['SUCCESS_MSG']."</div>";	}elseif($isSuccess == OPERATION_FAIL){		$err .= "<div class='error'>".$_lang['tbl_order_promotions'][$action]['FAILURE_MSG']."</div>";	}elseif($isSuccess == OPERATION_DUPLICATE){		$err .= "<div class='error'>".$_lang['tbl_order_promotions'][$action]['DUPLICATE_MSG']."</div>";	}}//..if	$result_found=0;	$tbl_order_promotionslist = $objtbl_order_promotions->readArray(array(OFFSET_TITLE=>$offset,LIMIT_TITLE=>$limit,SORT_BY=>$sort_by,SORT_ON=>$sort_on),$result_found,1);	$allpageCount = 0;	$currentPage = 0;	$smarty->assign('pagination',biz_pagination(array("url"=>$navigationURL,LIMIT_TITLE=>$limit,OFFSET_TITLE=>$offset,"count"=>$result_found),$allpageCount,$currentPage));	$smarty->assign('allpageCount',$allpageCount);	$smarty->assign('currentPage',$currentPage);	$smarty->assign('tbl_order_promotionslist', $tbl_order_promotionslist);	$smarty->assign('result_found',$result_found);	$template = "tbl_order_promotions/tbl_order_promotions.tpl";	if (is_not_empty($mode)){		if(($mode == MODE_VIEW || $mode==MODE_UPDATE) && is_gt_zero_num($ordprom_id)){			$tbl_order_promotionsinfo= $objtbl_order_promotions->GetInfo($ordprom_id);			$smarty->assign('tbl_order_promotionsinfo',$tbl_order_promotionsinfo);			if($mode==MODE_UPDATE){				$smarty->assign("isUpdate",1);			}			$template = "tbl_order_promotions/view.tpl";		}elseif($mode == MODE_CREATE){			$template = "tbl_order_promotions/create.tpl";					}}	$smarty->assign('page_url', $url);	$smarty->assign('navigationURL',$navigationURL);	$smarty->assign('new_sort',$new_sort);	$smarty->assign('active_page',$active_page);/*}else{	$template="index.tpl";}*/include_once("footer.php");?>