err .= "<div class='error'>".$_lang['tbl_social_media'][$action]['FAILURE_MSG']."</div>";	}elseif($isSuccess == OPERATION_DUPLICATE){		$err .= "<div class='error'>".$_lang['tbl_social_media'][$action]['DUPLICATE_MSG']."</div>";	}}//..if	$result_found=0;	$tbl_social_medialist = $objtbl_social_media->readArray(array(OFFSET_TITLE=>$offset,LIMIT_TITLE=>$limit,SORT_BY=>$sort_by,SORT_ON=>$sort_on),$result_found,1);	$allpageCount = 0;	$currentPage = 0;	$smarty->assign('pagination',biz_pagination(array("url"=>$navigationURL,LIMIT_TITLE=>$limit,OFFSET_TITLE=>$offset,"count"=>$result_found),$allpageCount,$currentPage));	$smarty->assign('allpageCount',$allpageCount);	$smarty->assign('currentPage',$currentPage);	$smarty->assign('tbl_social_medialist', $tbl_social_medialist);	$smarty->assign('result_found',$result_found);	$template = "tbl_social_media/tbl_social_media.tpl";	if (is_not_empty($mode)){		if(($mode == MODE_VIEW || $mode==MODE_UPDATE) && is_gt_zero_num($social_id)){			$tbl_social_mediainfo= $objtbl_social_media->GetInfo($social_id);			$smarty->assign('tbl_social_mediainfo',$tbl_social_mediainfo);			if($mode==MODE_UPDATE){				$smarty->assign("isUpdate",1);			}			$template = "tbl_social_media/view.tpl";		}elseif($mode == MODE_CREATE){			$template = "tbl_social_media/create.tpl";					}}	$smarty->assign('page_url', $url);	$smarty->assign('navigationURL',$navigationURL);	$smarty->assign('new_sort',$new_sort);	$smarty->assign('active_page',$active_page);/*}else{	$template="index.tpl";}*/include_once("footer.php");?>