<?phpinclude_once(dirname(dirname(__FILE__)).'/init.php');include_once('header.php');$active_page = 'tbl_tip'; if($sesslife==true){ $tip_id= get_input('tip_id');$tip_percent= get_input('tip_percent');$tip_start_date= get_input('tip_start_date');$tip_end_date= get_input('tip_end_date');$action = strtoupper(get_input(ACTION_TITLE));$mode = strtoupper(get_input(MODE_TITLE));$offset = get_input(OFFSET_TITLE,OFFSET_VALUE);$limit =  get_input(LIMIT_TITLE,LIMIT_VALUE);$sort_on = get_input(SORT_ON,'tip_id');$sort_by=$new_sort='';biz_set_sorting_var($sort_by,$new_sort);$url = $website.'/user/tbl_tip.php';$navigationURL = $url.'?'.SORT_ON.'='.$sort_on.'&'.SORT_BY.'='.$sort_by;$isSuccess = '';//..get list of selected items$sel_tip= get_input('sel_tip');$lst_sel_ids='';if(is_not_empty($sel_tip)){		$lst_sel_ids=biz_implode(',', array_keys($sel_tip));} $objtbl_tip= new tbl_tip();if(is_not_empty($action)){	switch($action){		case ACTION_CREATE: 			$isSuccess = $objtbl_tip->create($tip_percent, $tip_start_date, $tip_end_date);			break;		case ACTION_UPDATE: 			$isSuccess = $objtbl_tip->update($tip_id, $tip_percent, $tip_start_date, $tip_end_date);			break;		case ACTION_DELETE: 		 if(is_not_empty($lst_sel_ids)){		 	$isSuccess = $objtbl_tip->delete(array(TIP_ID=>$lst_sel_ids));		 }else{		 	$isSuccess = $objtbl_tip->delete(array(TIP_ID=>$tip_id));		 }						break;		case ACTION_ACTIVATE: 		if(is_not_empty($lst_sel_ids)){		 	$isSuccess = $objtbl_tip->activate($lst_sel_ids);		 }else{			$isSuccess = $objtbl_tip->activate($tip_id);		 }			break;		case ACTION_DEACTIVATE: 			if(is_not_empty($lst_sel_ids)){		 	$isSuccess = $objtbl_tip->deactivate($lst_sel_ids);		 }else{			$isSuccess = $objtbl_tip->deactivate($tip_id);		 }			break;	}//..switch}//..ifif(is_not_empty($isSuccess)){	if(is_gt_zero_num($isSuccess)){		$_SESSION[SES_FLASH_MSG] .= '<div class="success">'.$_lang['tbl_tip'][$action]['SUCCESS_MSG'].'</div>';	}elseif($isSuccess == OPERATION_FAIL){		$_SESSION[SES_FLASH_MSG] .= '<div class="error">'.$_lang['tbl_tip'][$action]['FAILURE_MSG'].'</div>';	}elseif($isSuccess == OPERATION_DUPLICATE){		$_SESSION[SES_FLASH_MSG] .= '<div class="info">'.$_lang['tbl_tip'][$action]['DUPLICATE_MSG'].'</div>';	}}//..if	$result_found=0;	$tbl_tiplist = $objtbl_tip->readArray(array(TIP_RESTAURENT => $_SESSION[SES_RESTAURANT],OFFSET_TITLE=>$offset,LIMIT_TITLE=>$limit,SORT_BY=>$sort_by,SORT_ON=>$sort_on),$result_found,1);	$allpageCount = 0;	$currentPage = 0;	$smarty->assign('pagination',biz_pagination(array('url'=>$navigationURL,LIMIT_TITLE=>$limit,OFFSET_TITLE=>$offset,'count'=>$result_found),$allpageCount,$currentPage));	$smarty->assign('allpageCount',$allpageCount);	$smarty->assign('currentPage',$currentPage);	$smarty->assign('tbl_tiplist', $tbl_tiplist);	$smarty->assign('result_found',$result_found);	$template = 'tbl_tip/tbl_tip.tpl';		$breadcrumbs[] =  array('link'=>$website.'/user/pref_mng_cntrols.php',						'title'=>$_lang['main']['pref_mng_cntrls']) ;	$breadcrumbs[] =  array('link'=>$website.'/user/tbl_tip.php',						'title'=>$_lang[TBL_TIP]['listing_title']) ;	if (is_not_empty($mode)){		if(($mode == MODE_VIEW || $mode==MODE_UPDATE) && is_gt_zero_num($tip_id)){			$tbl_tipinfo= $objtbl_tip->GetInfo($tip_id);			$smarty->assign('tbl_tipinfo',$tbl_tipinfo);			if($mode==MODE_UPDATE){				$smarty->assign('isUpdate',1);			}			$template = 'tbl_tip/view.tpl';			$breadcrumbs[] =  array('link'=>$website.'/user/tbl_tip.php?'.MODE_TITLE.'='.$mode.'&'.TIP_ID.'='.$tip_id,						'title'=>$tbl_tipinfo[TIP_PERCENT].$_lang['percent_mark']) ;		}elseif($mode == MODE_CREATE){			$template = 'tbl_tip/create.tpl'; 	  	$breadcrumbs[] =  array('link'=>$website.'/user/tbl_tip.php?'.MODE_TITLE.'='.MODE_CREATE,						'title'=>$_lang[TBL_TIP]['create_title']) ;		}}	$smarty->assign('page_url', $url);	$smarty->assign('navigationURL',$navigationURL);	$smarty->assign('new_sort',$new_sort);	$smarty->assign('active_page',$active_page); }else{	$template='index.tpl';} include_once('footer.php');?>